    let [show ,setShow]=useState(false)
  return (
    <div className='thread-container' >
        <FaHamburger
        className='burger'
        style={{curs}}
        onClick={()=>setShow(true)}
        onDoubleClick={()=>setShow(false)}
        />
        {show  &&<Threadheader/>}
    </div>
  )
}

export default Threadcontainer
