function Thread() {
    let [show ,setShow]=useState(false);

  return (
    <div className='thread' >
        <FaBars
        style={{cursor:'pointer'}}
        onClick={()=>setShow(true)}
        onDoubleClick={()=>setShow(false)}
        />
        {show &&<Sidebar/>}
          <div className='thread-container'>

        <Threadheader/>

        </div>
    </div>
  )
}

export default Thread
