import React, { useState } from 'react'
import { FaBars } from 'react-icons/fa'
import Sidebar from '../components/Sidebar'

function Thread() {
    let [show ,setShow]=useState(false);
  return (
    <div className='thread' >
        <FaBars
        onClick={()=>setShow(true)}
        />
        {show &&<Sidebar/>}
        Thread
    </div>
  )
}

export default Thread
